    <h1  class=\"mt-4 ml-2\">Détail de la candidature</h1>
    
    <div class=\"container\">
    <table class=\"table mt-5\">
        <tbody>
            <tr>
                <th>Nom du poste</th>
                <td>{{ job.jobtitle }}</td>
            </tr>
            <tr>
                <th>Nom de l'entreprise</th>
                <td>{{ job.companytitle }}</td>
            </tr>
            <tr>
                <th>Date de candidature</th>
                <td>{{ job.dateapplication ? job.dateapplication|date('d/m/Y',\"Europe/Paris\") : '' }}</td>
            </tr>

            <tr>
                <th>Contact de l'entreprise</th>
                <td>{{ job.contact }}</td>
            </tr>
            <tr>
                <th>Lien de l'annonce</th>
                <td><a href=\"{{ job.url }}\">{{ job.url }}</a></td>
            </tr>
             <tr>
                <th>Etat de la candidature</th>
                <td>{{ job.feedback }}</td>
            </tr>
        </tbody>
    </table>
    <a href=\"{{ path('job_index') }}\">Retour à la liste des candidatures</a>
    </div>
{% endblock %}
